import { defineStore } from "pinia"

export const bookStore = defineStore("books", {

    state: () => {

        return {

            books: null,
            lastSeen: null,
            searchResult: null,
            highlights: null

        }

    },

    actions:{

        async initBooks()
        {
  
            const req = await fetch("http://localhost:3000/books")
        
            this.books = await req.json()
        
        },

        home(){
            this.searchResult = this.highlights
        }


    }


})
