import { defineStore } from "pinia"

export const cartStore = defineStore("cart", {

    state: () => {

        return {

            items: null

        }

    },

    actions:{

        addCart(book)
        {

            if(this.items){

                let inCart = false

                for(let item of this.items)
                {
                    if(item.bookId === book.bookId) 
                    {

                        item.qtde += 1
                        inCart = true
                        break
                    
                    }

                }

                if(!inCart) 
                {
                    book.qtde = 1
                    this.items.push(book)
                }

            }else{

                book.qtde = 1
                this.items = [book]
            }

        }
    }

})
