import { defineStore } from "pinia";
export const menuStore = defineStore("menu", {
  state: () => {
    return {
      home: false,
      contato: false,
      sobre: false,
      livros: false
    };
  },
  actions: {
    menu(page) {
      switch (page) {
        case "Home":
          this.home = true;
          this.contato = false;
          this.sobre = false;
          this.livros = false;
          break;
        case "Contato":
          this.home = false;
          this.contato = true;
          this.sobre = false;
          this.livros = false;
          break;
        case "Sobre":
          this.home = false;
          this.contato = false;
          this.sobre = true;
          this.livros = false;
          break;
        case "Livros":
          this.home = false;
          this.contato = false;
          this.sobre = false;
          this.livros = true;
          break;
        default:
          this.home = false;
          this.contato = false;
          this.sobre = false;
          this.livros = false;
          break;
      }
    }
  }
});
