import { defineStore } from "pinia"

export const sessionStore = defineStore("session", {

    state: () => {

        return {

            isLoggedIn: true,
            user: {admin: true}

        }

    },

    actions:{

        logout(){

            this.isLoggedIn = false
            this.user = {admin: false}

        }

    }


})
